sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	isDatePast: function(strDate){
		//Create GlideDateTime object for the current date and time and the passed in date
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);


		//If the testdate is before rightnow, return true, else return false
		if (testdate.before(rightnow)){
			return true;
		}
		else {
			return false;
		}
	},

	isDateToday: function(strDate){
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);
		//Create GlideDate object for the current date and the passed in date
		var today = rightnow.getLocalDate();
		var istoday = testdate.getLocalDate();

		//If the testdate is the same as today, return true, else return false
		if (istoday.compareTo(today) == 0){
			return true;
		}
		else {
			return false;
		}
	},

    type: 'NeedItUtils'
};
